@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SignUpScreen(
 onSignUpClick: (String, String, String) -> Unit = { _, _, _ -> },
 onSignInClick: () -> Unit = {},
 modifier: Modifier = Modifier
) {
 var fullName by remember { mutableStateOf("") }
 var email by remember { mutableStateOf("") }
 var password by remember { mutableStateOf("") }
 var confirmPassword by remember { mutableStateOf("") }
 var passwordVisible by remember { mutableStateOf(false) }
 var confirmPasswordVisible by remember { mutableStateOf(false) }
 var acceptTerms by remember { mutableStateOf(false) }

 var isNameError by remember { mutableStateOf(false) }
 var isEmailError by remember { mutableStateOf(false) }
 var isPasswordError by remember { mutableStateOf(false) }
 var isConfirmPasswordError by remember { mutableStateOf(false) }
 var passwordMismatch by remember { mutableStateOf(false) }

 Column(
 modifier = modifier
 .fillMaxSize()
 .padding(24.dp)
 .verticalScroll(rememberScrollState()),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(modifier = Modifier.height(32.dp))

 // App Logo/Title
 Text(
 text = "",
 fontSize = 48.sp,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 Text(
 text = "Create Account",
 style = MaterialTheme.typography.headlineLarge,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "Join the mystical journey",
 style = MaterialTheme.typography.bodyMedium,
 color = MaterialTheme.colorScheme.onSurfaceVariant,
 textAlign = TextAlign.Center,
 modifier = Modifier.padding(bottom = 32.dp)
 )

 // Full Name Field
 OutlinedTextField(
 value = fullName,
 onValueChange = { 
 fullName = it
 isNameError = false
 },
 label = { Text("Full Name") },
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Person,
 contentDescription = "Name Icon"
 )
 },
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Text,
 imeAction = ImeAction.Next
 ),
 isError = isNameError,
 supportingText = if (isNameError) {
 { Text("Please enter your full name") }
 } else null,
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 16.dp)
 )

 // Email Field
 OutlinedTextField(
 value = email,
 onValueChange = { 
 email = it
 isEmailError = false
 },
 label = { Text("Email") },
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Email,
 contentDescription = "Email Icon"
 )
 },
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Email,
 imeAction = ImeAction.Next
 ),
 isError = isEmailError,
 supportingText = if (isEmailError) {
 { Text("Please enter a valid email") }
 } else null,
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 16.dp)
 )

 // Password Field
 OutlinedTextField(
 value = password,
 onValueChange = { 
 password = it
 isPasswordError = false
 passwordMismatch = false
 },
 label = { Text("Password") },
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Lock,
 contentDescription = "Password Icon"
 )
 },
 trailingIcon = {
 IconButton(onClick = { passwordVisible = !passwordVisible }) {
 Icon(
 imageVector = if (passwordVisible) Icons.Default.Visibility else Icons.Default.VisibilityOff,
 contentDescription = if (passwordVisible) "Hide password" else "Show password"
 )
 }
 },
 visualTransformation = if (passwordVisible) VisualTransformation.None else PasswordVisualTransformation(),
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Password,
 imeAction = ImeAction.Next
 ),
 isError = isPasswordError,
 supportingText = if (isPasswordError) {
 { Text("Password must be at least 6 characters") }
 } else null,
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 16.dp)
 )

 // Confirm Password Field
 OutlinedTextField(
 value = confirmPassword,
 onValueChange = { 
 confirmPassword = it
 isConfirmPasswordError = false
 passwordMismatch = false
 },
 label = { Text("Confirm Password") },
 leadingIcon = {
 Icon(
 imageVector = Icons.Default.Lock,
 contentDescription = "Confirm Password Icon"
 )
 },
 trailingIcon = {
 IconButton(onClick = { confirmPasswordVisible = !confirmPasswordVisible }) {
 Icon(
 imageVector = if (confirmPasswordVisible) Icons.Default.Visibility else Icons.Default.VisibilityOff,
 contentDescription = if (confirmPasswordVisible) "Hide password" else "Show password"
 )
 }
 },
 visualTransformation = if (confirmPasswordVisible) VisualTransformation.None else PasswordVisualTransformation(),
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Password,
 imeAction = ImeAction.Done
 ),
 isError = isConfirmPasswordError || passwordMismatch,
 supportingText = if (isConfirmPasswordError) {
 { Text("Please confirm your password") }
 } else if (passwordMismatch) {
 { Text("Passwords do not match") }
 } else null,
 modifier = Modifier
 .fillMaxWidth()
