package com.example.tarot.ui.screens.home

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.LazyRow
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.History
import androidx.compose.material.icons.filled.Menu
import androidx.compose.material.icons.filled.Person
import androidx.compose.material.icons.filled.Settings
import androidx.compose.material.icons.filled.Star
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.graphics.Brush
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.tooling.preview.Preview
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import com.example.tarot.ui.theme.TarotTheme
import kotlinx.coroutines.launch

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun HomeScreen(
 onNavigateToReading: (String) -> Unit = {},
 onNavigateToHistory: () -> Unit = {},
 onNavigateToProfile: () -> Unit = {},
 modifier: Modifier = Modifier
) {
 val drawerState = rememberDrawerState(DrawerValue.Closed)
 val scope = rememberCoroutineScope()

 ModalNavigationDrawer(
 drawerState = drawerState,
 drawerContent = {
 NavigationDrawerContent(
 onNavigateToHistory = {
 scope.launch { drawerState.close() }
 onNavigateToHistory()
 },
 onNavigateToProfile = {
 scope.launch { drawerState.close() }
 onNavigateToProfile()
 }
 )
 }
 ) {
 Scaffold(
 topBar = {
 TopAppBar(
 title = {
 Text(
 text = "Tarot Readings",
 fontWeight = FontWeight.Bold
 )
 },
 navigationIcon = {
 IconButton(
 onClick = { scope.launch { drawerState.open() } }
 ) {
 Icon(
 imageVector = Icons.Default.Menu,
 contentDescription = "Menu"
 )
 }
 },
 colors = TopAppBarDefaults.topAppBarColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer
 )
 )
 }
 ) { paddingValues ->
 LazyColumn(
 modifier = modifier
 .fillMaxSize()
 .padding(paddingValues)
 .padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(24.dp)
 ) {
 item {
 WelcomeCard()
 }

 item {
 ReadingTypesSection(onNavigateToReading = onNavigateToReading)
 }

 item {
 DailyInsightCard()
 }

 item {
 QuickActionsSection(
 onNavigateToReading = onNavigateToReading,
 onNavigateToHistory = onNavigateToHistory
 )
 }
 }
 }
 }
}

@Composable
fun NavigationDrawerContent(
 onNavigateToHistory: () -> Unit,
 onNavigateToProfile: () -> Unit,
 modifier: Modifier = Modifier
) {
 ModalDrawerSheet(
 modifier = modifier.width(280.dp)
 ) {
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp)
 ) {
 // Header
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(120.dp)
 .clip(RoundedCornerShape(12.dp))
 .background(
 brush = Brush.horizontalGradient(
 colors = listOf(
 MaterialTheme.colorScheme.primary,
 MaterialTheme.colorScheme.secondary
 )
 )
 ),
 contentAlignment = Alignment.Center
 ) {
 Column(
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = "",
 fontSize = 32.sp
 )
 Text(
 text = "Mystic Tarot",
 style = MaterialTheme.typography.titleLarge,
 color = MaterialTheme.colorScheme.onPrimary,
 fontWeight = FontWeight.Bold
 )
 }
 }

 Spacer(modifier = Modifier.height(24.dp))

 // Navigation Items
 NavigationDrawerItem(
 icon = { Icon(Icons.Default.History, contentDescription = null) },
 label = { Text("Reading History") },
 selected = false,
 onClick = onNavigateToHistory
 )

 NavigationDrawerItem(
 icon = { Icon(Icons.Default.Person, contentDescription = null) },
 label = { Text("Profile") },
 selected = false,
 onClick = onNavigateToProfile
 )

 NavigationDrawerItem(
 icon = { Icon(Icons.Default.Settings, contentDescription = null) },
 label = { Text("Settings") },
 selected = false,
 onClick = { }
 )
 }
 }
}

@Composable
fun WelcomeCard(
 modifier: Modifier = Modifier
) {
 Card(
 modifier = modifier.fillMaxWidth(),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.primaryContainer
 )
 ) {
 Column(
 modifier = Modifier.padding(20.dp),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = "",
 fontSize = 32.sp,
 modifier = Modifier.padding(bottom = 8.dp)
 )
 Text(
 text = "Welcome to Your Mystical Journey",
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold,
 textAlign = TextAlign.Center,
 color = MaterialTheme.colorScheme.onPrimaryContainer
 )
 Text(
 text = "Discover the ancient wisdom of tarot cards and unlock the secrets of your destiny",
 style = MaterialTheme.typography.bodyMedium,
 textAlign = TextAlign.Center,
 color = MaterialTheme.colorScheme.onPrimaryContainer,
 modifier = Modifier.padding(top = 8.dp)
 )
 }
 }
}

@Composable
fun ReadingTypesSection(
 onNavigateToReading: (String) -> Unit,
 modifier: Modifier = Modifier
) {
 Column(modifier = modifier) {
 Text(
 text = "Choose Your Reading",
 style = MaterialTheme.typography.titleLarge,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 items(readingTypes) { readingType ->
 ReadingTypeCard(
 readingType = readingType,
 onClick = { onNavigateToReading(readingType.type) }
 )
 }
 }
 }
}

@Composable
fun ReadingTypeCard(
 readingType: ReadingType,
 onClick: () -> Unit,
 modifier: Modifier = Modifier
) {
 Card(
 onClick = onClick,
 modifier = modifier.width(160.dp),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.surface
 ),
 elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
 ) {
 Column(
 modifier = Modifier.padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = readingType.emoji,
 fontSize = 32.sp,
 modifier = Modifier.padding(bottom = 8.dp)
 )
 Text(
 text = readingType.title,
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 textAlign = TextAlign.Center
 )
 Text(
 text = readingType.description,
 style = MaterialTheme.typography.bodySmall,
 textAlign = TextAlign.Center,
 color = MaterialTheme.colorScheme.onSurfaceVariant,
 modifier = Modifier.padding(top = 4.dp)
 )
 }
 }
}

@Composable
fun DailyInsightCard(
 modifier: Modifier = Modifier
) {
 Card(
 modifier = modifier.fillMaxWidth(),
 colors = CardDefaults.cardColors(
 containerColor = MaterialTheme.colorScheme.secondaryContainer
 )
 ) {
 Column(
 modifier = Modifier.padding(20.dp)
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically,
 modifier = Modifier.padding(bottom = 12.dp)
 ) {
 Text(
 text = "",
 fontSize = 24.sp,
 modifier = Modifier.padding(end = 8.dp)
 )
 Text(
 text = "Daily Insight",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onSecondaryContainer
 )
 }
 Text(
 text = "\"Trust your intuition today. The universe is guiding you toward new opportunities.\"",
 style = MaterialTheme.typography.bodyMedium,
 color = MaterialTheme.colorScheme.onSecondaryContainer,
 modifier = Modifier.padding(bottom = 8.dp)
 )
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.End
 ) {
 repeat(5) { index ->
 Icon(
 imageVector = Icons.Default.Star,
 contentDescription = null,
 tint = if (index < 4) MaterialTheme.colorScheme.primary else MaterialTheme.colorScheme.outline,
 modifier = Modifier.size(16.dp)
 )
 }
 }
 }
 }
}

@Composable
fun QuickActionsSection(
 onNavigateToReading: (String) -> Unit,
 onNavigateToHistory: () -> Unit,
 modifier: Modifier = Modifier
) {
 Column(modifier = modifier) {
 Text(
 text = "Quick Actions",
 style = MaterialTheme.typography.titleLarge,
 fontWeight = FontWeight.Bold,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Button(
 onClick = { onNavigateToReading("quick") },
 modifier = Modifier.weight(1f)
 ) {
 Text("Quick Reading")
 }

 OutlinedButton(
 onClick = onNavigateToHistory,
 modifier = Modifier.weight(1f)
 ) {
 Text("View History")
 }
 }
 }
}

