@Composable
fun TarotNavigation(
 authViewModel: AuthViewModel
) {
 val authUiState by authViewModel.uiState.collectAsState()
 var currentScreen by remember { mutableStateOf<Screen>(Screen.Login) }

 // Navigate based on authentication state
 LaunchedEffect(authUiState.isLoggedIn) {
 if (authUiState.isLoggedIn) {
 // Only navigate to home if we're currently on an auth screen
 if (currentScreen is Screen.Login || currentScreen is Screen.SignUp || currentScreen is Screen.TestAccounts) {
 currentScreen = Screen.Home
 }
 } else {
 // Only navigate to login if we're not already on an auth screen
 if (currentScreen !is Screen.Login && currentScreen !is Screen.SignUp && currentScreen !is Screen.TestAccounts) {
 currentScreen = Screen.Login
 }
 }
