sealed class Screen {
 object Login : Screen()
 object SignUp : Screen()
 object Home : Screen()
 object Profile : Screen()
 object TestAccounts : Screen()
 object DailyReading : Screen()
 object AskQuestion : Screen()
 object History : Screen()
