 TarotTheme {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(MysticDarkBlue)
 .padding(24.dp),
 contentAlignment = Alignment.Center
 ) {
 QuestionInput(
 question = "",
 onQuestionChange = {},
 onAskQuestion = {}
 )
 }
 }
