@Composable
fun TarotNavigation(
 authViewModel: AuthViewModel
) {
 val authUiState by authViewModel.uiState.collectAsState()
 var currentScreen by remember { mutableStateOf<Screen>(Screen.Login) }

 // Simple approach: only handle login success
 if (authUiState.isLoggedIn && currentScreen == Screen.Login) {
 currentScreen = Screen.Home
 }

 // Handle logout
 if (!authUiState.isLoggedIn && currentScreen !in listOf(
 Screen.Login,
 Screen.SignUp,
 Screen.TestAccounts
 )
 ) {
 currentScreen = Screen.Login
 }

 Scaffold(
 modifier = Modifier.fillMaxSize()
 ) { innerPadding ->
 when (currentScreen) {
 is Screen.Login -> {
 LoginScreen(
 onLoginClick = { email, password ->
 authViewModel.login(email, password)
 },
 onSignUpClick = {
 currentScreen = Screen.SignUp
 },
 onForgotPasswordClick = {
 // Handle forgot password
 },
 onTestAccountsClick = {
 currentScreen = Screen.TestAccounts
 },
 authUiState = authUiState,
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.SignUp -> {
 SignUpScreen(
 onSignUpClick = { name, email, password ->
 authViewModel.signUp(name, email, password)
 },
 onSignInClick = {
 currentScreen = Screen.Login
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.Home -> {
 HomeScreen(
 onNavigateToReading = { readingType ->
 currentScreen = when (readingType) {
 "daily" -> Screen.DailyReading
 "question" -> Screen.AskQuestion
 else -> Screen.DailyReading
 }
 },
 onNavigateToHistory = {
 currentScreen = Screen.History
 },
