@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun DailyReadingScreen(
 onBackClick: () -> Unit = {},
 modifier: Modifier = Modifier
) {
 var isCardRevealed by remember { mutableStateOf(false) }
 var showInterpretation by remember { mutableStateOf(false) }

 val cardRotation by animateFloatAsState(
 targetValue = if (isCardRevealed) 180f else 0f,
 animationSpec = tween(800),
 label = "cardFlip"
 )

 Scaffold(
 modifier = modifier.fillMaxSize(),
 topBar = {
 TopAppBar(
 title = {
 Text(
 text = "Daily Reading",
 color = TextPrimary
 )
 },
 navigationIcon = {
 IconButton(onClick = onBackClick) {
 Icon(
 imageVector = Icons.Default.ArrowBack,
 contentDescription = "Back",
 tint = TextPrimary
 )
 }
 },
 colors = TopAppBarDefaults.topAppBarColors(
 containerColor = MysticDarkBlue,
 titleContentColor = TextPrimary,
 navigationIconContentColor = TextPrimary
 )
 )
 },
 containerColor = MysticDarkBlue
 ) { paddingValues ->
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 BackgroundStart,
 BackgroundEnd
 )
 )
 )
 ) {
 // Main Content
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(24.dp)
 .verticalScroll(rememberScrollState()),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(modifier = Modifier.height(32.dp))

 // Daily Reading Title
 Text(
 text = "Your Daily Guidance",
 fontSize = 28.sp,
 fontWeight = FontWeight.Bold,
 color = TextAccent,
 textAlign = TextAlign.Center,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "Tap the card to reveal your message",
 fontSize = 16.sp,
 color = TextSecondary,
 textAlign = TextAlign.Center,
 modifier = Modifier.padding(bottom = 48.dp)
 )

 // Tarot Card
 Box(
 modifier = Modifier
 .size(width = 220.dp, height = 350.dp)
 .clickable {
 if (!isCardRevealed) {
 isCardRevealed = true
 showInterpretation = true
 }
 }
 .graphicsLayer {
 rotationY = cardRotation
 cameraDistance = 12f * density
 },
 contentAlignment = Alignment.Center
 ) {
 if (cardRotation <= 90f) {
 // Card Back
 MysticCardBack()
 } else {
 // Card Front - The Sun card as example
 MysticCardFront(
 cardName = "The Sun",
 cardImage = ""
 )
 }
 }

 Spacer(modifier = Modifier.height(48.dp))

 // Card Interpretation
 if (showInterpretation) {
 CardInterpretation()
 }

 Spacer(modifier = Modifier.height(32.dp))
 }
 }
 }
}

@Composable
fun MysticCardBack() {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .clip(RoundedCornerShape(20.dp))
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 CardBack,
 MysticNavy
 )
 )
 )
 .border(
 width = 3.dp,
 color = CardBorder,
 shape = RoundedCornerShape(20.dp)
 ),
 contentAlignment = Alignment.Center
 ) {
 // Card back design
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center
 ) {
 Text(
 text = "",
 fontSize = 48.sp,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 Text(
 text = "MYSTICA",
 fontSize = 18.sp,
 fontWeight = FontWeight.Bold,
 color = TextAccent,
 letterSpacing = 4.sp
 )

 Box(
 modifier = Modifier
 .size(40.dp, 2.dp)
 .background(MysticGold)
 .padding(vertical = 8.dp)
 )

 Text(
 text = " ",
 fontSize = 16.sp,
 color = MysticSilver,
 modifier = Modifier.padding(top = 16.dp)
 )
 }
 }
}

@Composable
fun MysticCardFront(
 cardName: String,
 cardImage: String
) {
 Box(
 modifier = Modifier
 .fillMaxSize()
 .clip(RoundedCornerShape(20.dp))
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 MysticLightGold,
 TextPrimary
 )
 )
 )
 .border(
 width = 3.dp,
 color = CardBorder,
 shape = RoundedCornerShape(20.dp)
 )
 .graphicsLayer {
 rotationY = 180f // Flip to show correctly
 },
 contentAlignment = Alignment.Center
 ) {
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Center
 ) {
 Text(
 text = cardImage,
 fontSize = 72.sp,
 modifier = Modifier.padding(bottom = 24.dp)
 )

 Text(
 text = cardName,
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = MysticDarkBlue,
 textAlign = TextAlign.Center
 )
 }
 }
}

@Composable
fun CardInterpretation() {
 Card(
 modifier = Modifier.fillMaxWidth(),
 colors = CardDefaults.cardColors(
 containerColor = MysticNavy.copy(alpha = 0.9f)
 ),
 shape = RoundedCornerShape(20.dp)
 ) {
 Column(
 modifier = Modifier.padding(24.dp)
 ) {
 Text(
 text = "The Sun",
 fontSize = 24.sp,
 fontWeight = FontWeight.Bold,
 color = TextAccent,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 Text(
 text = "Meaning",
 fontSize = 16.sp,
 fontWeight = FontWeight.SemiBold,
 color = MysticGold,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "The Sun represents joy, success, celebration, and positivity. This card brings optimism and good fortune to your day. You are entering a period of happiness and achievement.",
 fontSize = 14.sp,
 color = TextPrimary,
 lineHeight = 20.sp,
 modifier = Modifier.padding(bottom = 16.dp)
 )

 Text(
 text = "Today's Message",
 fontSize = 16.sp,
 fontWeight = FontWeight.SemiBold,
 color = MysticGold,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "Embrace the positive energy around you. Share your light with others and watch how it multiplies. Success is within reach - trust in your abilities and let your authentic self shine through.",
 fontSize = 14.sp,
 color = TextPrimary,
 lineHeight = 20.sp
 )
 }
 }
}

