fun DailyReadingScreenPreview() {
 TarotTheme {
 DailyReadingScreen()
 }
}

@Preview(showBackground = true, name = "Card Back Preview")
@Composable
fun MysticCardBackPreview() {
 TarotTheme {
 Box(
 modifier = Modifier
 .size(220.dp, 350.dp)
 .background(MysticDarkBlue),
 contentAlignment = Alignment.Center
 ) {
 MysticCardBack()
 }
 }
}

@Preview(showBackground = true, name = "Card Front Preview")
@Composable
fun MysticCardFrontPreview() {
 TarotTheme {
 Box(
 modifier = Modifier
 .size(220.dp, 350.dp)
 .background(MysticDarkBlue),
 contentAlignment = Alignment.Center
 ) {
 MysticCardFront("The Sun", "")
 }
 }
}

