@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun AskQuestionScreen(
 onBackClick: () -> Unit = {},
 modifier: Modifier = Modifier
) {
 var question by remember { mutableStateOf("") }
 var isCardRevealed by remember { mutableStateOf(false) }
 var showInterpretation by remember { mutableStateOf(false) }
 var hasAskedQuestion by remember { mutableStateOf(false) }

 // Function to reset the screen state
 fun resetScreen() {
 question = ""
 isCardRevealed = false
 showInterpretation = false
 hasAskedQuestion = false
 }

 val cardRotation by animateFloatAsState(
 targetValue = if (isCardRevealed) 180f else 0f,
 animationSpec = tween(800),
 label = "cardFlip"
 )

 Scaffold(
 modifier = modifier.fillMaxSize(),
 topBar = {
 TopAppBar(
 title = {
 Text(
 text = "Ask a Question",
 color = TextPrimary
 )
 },
 navigationIcon = {
 IconButton(onClick = onBackClick) {
 Icon(
 imageVector = Icons.Default.ArrowBack,
 contentDescription = "Back",
 tint = TextPrimary
 )
 }
 },
 colors = TopAppBarDefaults.topAppBarColors(
 containerColor = MysticDarkBlue,
 titleContentColor = TextPrimary,
 navigationIconContentColor = TextPrimary
 )
 )
 },
 containerColor = MysticDarkBlue
 ) { paddingValues ->
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues)
 .background(
 brush = Brush.verticalGradient(
 colors = listOf(
 BackgroundStart,
 BackgroundEnd
 )
 )
 )
 ) {
 // Main Content
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(24.dp)
 .verticalScroll(rememberScrollState()),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(modifier = Modifier.height(32.dp))

 // Question Section
 if (!hasAskedQuestion) {
 QuestionInput(
 question = question,
 onQuestionChange = { question = it },
 onAskQuestion = {
 if (question.isNotBlank()) {
 hasAskedQuestion = true
 }
 }
 )
 } else {
 // Show the question that was asked
 Card(
 modifier = Modifier
 .fillMaxWidth()
 .padding(bottom = 32.dp),
 colors = CardDefaults.cardColors(
 containerColor = MysticPurple.copy(alpha = 0.6f)
 ),
 shape = RoundedCornerShape(16.dp)
 ) {
 Column(
 modifier = Modifier.padding(20.dp)
 ) {
 Text(
 text = "Your Question:",
 fontSize = 14.sp,
 fontWeight = FontWeight.SemiBold,
 color = MysticGold,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "\"$question\"",
 fontSize = 16.sp,
 color = TextPrimary,
 fontStyle = androidx.compose.ui.text.font.FontStyle.Italic,
 lineHeight = 22.sp
 )
 }
 }

 // Reading Title
 Text(
 text = "The Universe Responds",
 fontSize = 28.sp,
 fontWeight = FontWeight.Bold,
 color = TextAccent,
 textAlign = TextAlign.Center,
 modifier = Modifier.padding(bottom = 8.dp)
 )

 Text(
 text = "Tap the card to reveal your guidance",
 fontSize = 16.sp,
 color = TextSecondary,
 textAlign = TextAlign.Center,
 modifier = Modifier.padding(bottom = 48.dp)
 )

 // Tarot Card
 Box(
 modifier = Modifier
 .size(width = 220.dp, height = 350.dp)
 .clickable {
 if (!isCardRevealed) {
 isCardRevealed = true
 showInterpretation = true
 }
 }
 .graphicsLayer {
 rotationY = cardRotation
 cameraDistance = 12f * density
 },
 contentAlignment = Alignment.Center
 ) {
 if (cardRotation <= 90f) {
 // Card Back
 com.example.tarot.ui.screens.reading.MysticCardBack()
 } else {
 // Card Front - The High Priestess as example
 com.example.tarot.ui.screens.reading.MysticCardFront(
 cardName = "The High Priestess",
 cardImage = ""
 )
 }
 }

 Spacer(modifier = Modifier.height(48.dp))
 }

 // Card Interpretation
 if (showInterpretation) {
 QuestionCardInterpretation(
 question = question,
 onAskAnother = { resetScreen() }
 )
 }
 }
 }
 }
}

