+ .padding(vertical = 8.dp)
+ )
+
+ Text(
+ text = " ",
+ fontSize = 16.sp,
+ color = MysticSilver,
+ modifier = Modifier.padding(top = 16.dp)
+ )
+ }
+ }
+}
+
+/**
+ * Reusable Tarot Card Front component
+ * Used across all reading screens with customizable card data
+ *
+ * @param cardName The name of the tarot card
+ * @param cardImage The emoji or symbol representing the card
+ * @param isReversed Whether the card is in reversed position (optional)
+ * @param modifier Modifier for customization
+ */
+@Composable
+fun MysticCardFront(
+ cardName: String,
+ cardImage: String,
+ isReversed: Boolean = false,
+ modifier: Modifier = Modifier
+) {
+ Box(
+ modifier = modifier
+ .fillMaxSize()
+ .clip(RoundedCornerShape(20.dp))
+ .background(
+ brush = Brush.verticalGradient(
+ colors = listOf(
+ MysticLightGold,
+ TextPrimary
+ )
+ )
+ )
+ .border(
+ width = 3.dp,
+ color = CardBorder,
+ shape = RoundedCornerShape(20.dp)
+ )
+ .graphicsLayer {
+ rotationY = 180f // Flip to show correctly
+ rotationZ = if (isReversed) 180f else 0f // Reverse card if needed
+ },
+ contentAlignment = Alignment.Center
+ ) {
+ Column(
+ horizontalAlignment = Alignment.CenterHorizontally,
+ verticalArrangement = Arrangement.Center
+ ) {
+ Text(
+ text = cardImage,
+ fontSize = 72.sp,
+ modifier = Modifier.padding(bottom = 24.dp)
+ )
+
+ Text(
+ text = cardName,
+ fontSize = 24.sp,
+ fontWeight = FontWeight.Bold,
+ color = MysticDarkBlue,
+ textAlign = TextAlign.Center
+ )
+
+ // Show reversed indicator if applicable
+ if (isReversed) {
+ Text(
+ text = "(Reversed)",
+ fontSize = 12.sp,
+ color = MysticDarkBlue.copy(alpha = 0.7f),
+ modifier = Modifier.padding(top = 4.dp)
+ )
+ }
+ }
+ }
+}
+
+/**
+ * Data class for Tarot Card information
+ * This will be useful when you integrate with a database
+ */
+data class TarotCardData(
+ val id: String,
+ val name: String,
+ val suit: String? = null, // Major Arcana cards don't have suits
+ val number: Int? = null,
+ val keywords: List<String> = emptyList(),
+ val uprightMeaning: String = "",
+ val reversedMeaning: String = "",
+ val description: String = "",
+ val imageSymbol: String = "", // Default symbol
+ val isReversed: Boolean = false
+)
+
+/**
+ * Enhanced Tarot Card Front component that uses TarotCardData
+ * This will be useful when you have a database of cards
+ */
+@Composable
+fun MysticCardFrontWithData(
+ cardData: TarotCardData,
+ modifier: Modifier = Modifier
+) {
+ MysticCardFront(
+ cardName = cardData.name,
+ cardImage = cardData.imageSymbol,
+ isReversed = cardData.isReversed,
+ modifier = modifier
+ )
+}
+
+/**
+ * Helper function to get card symbols based on card names
+ * This can be expanded when you have a full database
+ */
+fun getCardSymbol(cardName: String, suit: String? = null): String {
+ return when (cardName) {
+ "The Fool" -> ""
+ "The Magician" -> ""
+ "The High Priestess" -> ""
+ "The Empress" -> ""
+ "The Emperor" -> ""
+ "The Hierophant" -> ""
+ "The Lovers" -> ""
+ "The Chariot" -> ""
+ "Strength" -> ""
+ "The Hermit" -> ""
+ "Wheel of Fortune" -> ""
+ "Justice" -> ""
+ "The Hanged Man" -> ""
+ "Death" -> ""
+ "Temperance" -> ""
+ "The Devil" -> ""
+ "The Tower" -> ""
+ "The Star" -> ""
+ "The Moon" -> ""
+ "The Sun" -> ""
+ "Judgement" -> ""
+ "The World" -> ""
+ else -> when (suit) {
+ "Cups" -> ""
+ "Wands" -> ""
+ "Swords" -> ""
+ "Pentacles" -> ""
+ else -> ""
+ }
+ }
+}
+
