package com.example.tarot.ui.components

/**
 * Example of how to use the reusable MysticTarotCard components
 * with a database or data repository
 *
 * This file demonstrates best practices for:
 * 1. Using the TarotCardData class
 * 2. Creating a repository pattern
 * 3. Integrating with Room database (future)
 * 4. Using the components in different screens
 */

// Example Major Arcana cards data
object TarotCardDatabase {

 fun getMajorArcanaCards(): List<TarotCardData> {
 return listOf(
 TarotCardData(
 id = "0",
 name = "The Fool",
 suit = "Major Arcana",
 number = 0,
 keywords = listOf("New beginnings", "Innocence", "Adventure", "Potential"),
 uprightMeaning = "New beginnings, innocence, spontaneity, free spirit, originality, adventure, idealism, lack of commitment.",
 reversedMeaning = "Recklessness, taken advantage of, inconsistency, lack of direction, naive, poor judgment, lacking in experience.",
 description = "The Fool represents new beginnings and having faith in the future. Despite the name, it is not a negative card but represents the start of a new journey.",
 imageSymbol = ""
 ),
 TarotCardData(
 id = "1",
 name = "The Magician",
 suit = "Major Arcana",
 number = 1,
 keywords = listOf("Manifestation", "Resourcefulness", "Power", "Inspired action"),
 uprightMeaning = "Manifestation, resourcefulness, power, inspired action, creativity, confidence, potential, ability to achieve goals.",
 reversedMeaning = "Manipulation, poor planning, untapped talents, deception, lack of energy, confused goals, misuse of power.",
 description = "The Magician represents the power to manifest your desires through focused will and action.",
 imageSymbol = ""
 ),
 TarotCardData(
 id = "2",
 name = "The High Priestess",
 suit = "Major Arcana",
 number = 2,
 keywords = listOf("Intuition", "Sacred knowledge", "Subconscious mind", "Mystery"),
 uprightMeaning = "Intuition, sacred knowledge, divine feminine, the subconscious mind, higher purpose, mystery, spirituality.",
 reversedMeaning = "Secrets, disconnected from intuition, withdrawal, silence, repressed feelings, hidden motives.",
 description = "The High Priestess represents intuition, inner wisdom, and the mysterious aspects of the feminine divine.",
 imageSymbol = ""
 ),
 TarotCardData(
 id = "19",
 name = "The Sun",
 suit = "Major Arcana",
 number = 19,
 keywords = listOf("Joy", "Success", "Celebration", "Positivity"),
 uprightMeaning = "Joy, success, celebration, positivity, fun, warmth, success, vitality, optimism, enlightenment.",
 reversedMeaning = "Inner child, feeling down, overly optimistic, unrealistic expectations, lack of success, pessimism.",
 description = "The Sun represents joy, success, and positive energy. It brings light to any situation.",
 imageSymbol = ""
 )
 )
 }

 fun getCardById(id: String): TarotCardData? {
 return getMajorArcanaCards().find { it.id == id }
 }

 fun getRandomCard(): TarotCardData {
 return getMajorArcanaCards().random()
 }

 fun getRandomCards(count: Int): List<TarotCardData> {
 return getMajorArcanaCards().shuffled().take(count)
 }
}

/**
 * Repository pattern example for future database integration
 * This would typically interface with Room database
 */
class TarotCardRepository {

 // Future: This would connect to Room database
 // @Dao interface and Room database queries would go here

 suspend fun getAllCards(): List<TarotCardData> {
 // Simulate database delay
 kotlinx.coroutines.delay(500)
 return TarotCardDatabase.getMajorArcanaCards()
 }

 suspend fun getCardById(id: String): TarotCardData? {
 kotlinx.coroutines.delay(200)
 return TarotCardDatabase.getCardById(id)
 }

 suspend fun getRandomCard(): TarotCardData {
 kotlinx.coroutines.delay(300)
 return TarotCardDatabase.getRandomCard()
 }

 suspend fun getRandomCards(count: Int): List<TarotCardData> {
 kotlinx.coroutines.delay(400)
 return TarotCardDatabase.getRandomCards(count)
 }
}

