private fun getCardSymbol(card: TarotCardData): String {
 return when (card.name) {
 "The Fool" -> ""
 "The Magician" -> ""
 "The High Priestess" -> ""
 "The Empress" -> ""
 "The Emperor" -> ""
 "The Hierophant" -> ""
 "The Lovers" -> ""
 "The Chariot" -> ""
 "Strength" -> ""
 "The Hermit" -> ""
 "Wheel of Fortune" -> ""
 "Justice" -> ""
 "The Hanged Man" -> ""
 "Death" -> ""
 "Temperance" -> ""
 "The Devil" -> ""
 "The Tower" -> ""
 "The Star" -> ""
 "The Moon" -> ""
 "The Sun" -> ""
 "Judgement" -> ""
 "The World" -> ""
 else -> when (card.suit) {
 "Cups" -> ""
 "Wands" -> ""
 "Swords" -> ""
 "Pentacles" -> ""
 else -> ""
 }
 }
}

