 passwordMismatch = true
 false
 }
 else -> true
 }

 if (isValid) {
 onSignUpClick(fullName, email, password)
 }
 },
 enabled = acceptTerms,
 modifier = Modifier
 .fillMaxWidth()
 .height(56.dp),
