sealed class Screen {
 object Login : Screen()
 object SignUp : Screen()
 object ForgotPassword : Screen()
 object Home : Screen()
 object Profile : Screen()
 object DailyReading : Screen()
 object AskQuestion : Screen()
 object History : Screen()
}

@Composable
fun TarotNavigation(
 authViewModel: AuthViewModel
) {
 val authUiState by authViewModel.uiState.collectAsState()
 var currentScreen by remember { mutableStateOf<Screen>(Screen.Login) }

 // Simple approach: only handle login success
 if (authUiState.isLoggedIn && currentScreen == Screen.Login) {
 currentScreen = Screen.Home
 }

 // Handle logout
 if (!authUiState.isLoggedIn && currentScreen !in listOf(
 Screen.Login,
 Screen.SignUp,
 Screen.ForgotPassword
 )
 ) {
 currentScreen = Screen.Login
 }

 Scaffold(
 modifier = Modifier.fillMaxSize()
 ) { innerPadding ->
 when (currentScreen) {
 is Screen.Login -> {
 LoginScreen(
 onLoginClick = { email, password ->
 authViewModel.login(email, password)
 },
 onSignUpClick = {
 currentScreen = Screen.SignUp
 },
 onForgotPasswordClick = {
 currentScreen = Screen.ForgotPassword
 },
 authUiState = authUiState,
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.SignUp -> {
 SignUpScreen(
 onSignUpClick = { name, email, password ->
 authViewModel.signUp(name, email, password)
 },
 onSignInClick = {
 currentScreen = Screen.Login
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.ForgotPassword -> {
 ForgotPasswordScreen(
 onBackClick = {
 currentScreen = Screen.Login
 },
 onResetPasswordClick = { email ->
 // Handle password reset - for now just go back to login
 currentScreen = Screen.Login
 },
 onSuccessNavigation = {
 currentScreen = Screen.Login
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.Home -> {
 HomeScreen(
 onNavigateToReading = { readingType ->
 currentScreen = when (readingType) {
 "daily" -> Screen.DailyReading
 "question" -> Screen.AskQuestion
 else -> Screen.DailyReading
 }
 },
 onNavigateToHistory = {
 currentScreen = Screen.History
 },
 onNavigateToProfile = {
 currentScreen = Screen.Profile
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.Profile -> {
 ProfileScreen(
 onBackClick = {
 currentScreen = Screen.Home
 },
 onEditProfileClick = {
 // Handle edit profile
 },
 onLogoutClick = {
 authViewModel.logout()
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.DailyReading -> {
 DailyReadingScreen(
 onBackClick = {
 currentScreen = Screen.Home
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.AskQuestion -> {
 AskQuestionScreen(
 onBackClick = {
 currentScreen = Screen.Home
 },
 modifier = Modifier.padding(innerPadding)
 )
 }

 is Screen.History -> {
 // TODO: Implement history screen
 // For now, show a placeholder that goes back to home
 Box(
 modifier = Modifier
 .fillMaxSize()
 .padding(innerPadding),
 contentAlignment = Alignment.Center
 ) {
